import { useSelector } from 'react-redux'
import Blockies from 'react-blockies'

import logo from "../assets/logo.png";

const Navbar = () => {
    const account = useSelector(state => state.provider.account)
    const balance = useSelector(state => state.provider.balance)
  return (
    <div className="exchange__header grid">
      <div className="exchange__header--brand flex">
        <img src={logo} className="logo" alt="DApp Logo"></img>
        <h1>DApp Token Exchange</h1>
      </div>

      <div className="exchange__header--networks flex"></div>

      <div className="exchange__header--account flex">
        {balance ? (
          <p>
            <small>My Balance</small>
            {Number(balance).toFixed(4)}
          </p>
        ) : (
          <p>
            <small>0 ETH</small>
          </p>
        )}

        {/* example of truncated string */}
        {account ? (
          <a href="">{account.slice(0, 5) + "...." + account.slice(38, 44)}</a>
        ) : (
          <a href="">This account hasent loaded yet</a>
        )}
      </div>
    </div>
  );
};

export default Navbar;
