  useEffect(() => {
    loadBlockchainData()
  })

  return (
    <div>

      <Navbar></Navbar>

      <main className='exchange grid'>
        <section className='exchange__section--left grid'>

          <Markets></Markets>

          <Balance></Balance>

          <Order></Order>

        </section>
        <section className='exchange__section--right grid'>

          <PriceChart></PriceChart>

          <Transactions></Transactions>

          <Trades></Trades>

          <OrderBook></OrderBook>

        </section>
      </main>

      <Alert></Alert>

    </div>
  );
}

export default App;
